mapper.update:
update emp
username = #{username}, password = #{password}, name = #{name}, gender = #{gender}, image = #{image}, job = #{job}, entrydate = #{entrydate}, dept_id = #{deptId}, update_time = #{updateTime}
where id = #{id}

mapper.delete:
        delete from emp where id in
        #{id}
# collection:要遍历的集合名，item：遍历出的每个元素，separator:遍历的分隔符，open：遍历前拼接，close：遍历后拼接

mapper.select:
select *
from emp


        name like concat('%', #{name}, '%')


        and gender = #{gender}


        and entrydate between #{begin} and #{end}


limit #{start},#{pageSize}
